import { useState, useEffect } from "react";
import EditModal from "./EditModal";
import styled from "styled-components";
import CloudUploadOutlinedIcon from "@mui/icons-material/CloudUploadOutlined";
import CloseOutlinedIcon from "@mui/icons-material/CloseOutlined";
import { useDispatch } from "react-redux";
import { addProductapi, editProductapi } from "../redux/apiCalls/productsApis";

const Container = styled.div`
  margin: 1rem 0;
  display: grid;
  gap: 1rem;
  @media (max-width: 800px) {
    //added this bza left side was overflowing
    margin-left: 1em;
  }
`;
const Section = styled.div`
  display: flex;

  @media (max-width: 800px) {
    flex-direction: column;
    gap: 0.5rem;
  }
`;
const Left = styled.div`
  flex: 1;
`;
const Right = styled.div`
  flex: 2;
`;
const UploadImage = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border: 1px dashed black;
  padding: 1rem;
  label > svg {
    color: teal;
  }
`;
const ImagePreview = styled.img`
  width: 6rem;
  margin: 0.5rem 0;
`;
const UploadTitle = styled.span``;
const UploadDesc = styled.p`
  font-size: 0.7rem;
`;

const Textarea = styled.textarea`
  box-sizing: border-box;
  width: 100%;
  height: 500px;
  resize: vertical;
  padding: 0.9rem 0.5rem;
  background-color: #f4f5f7;
  border: 1px rgb(229, 231, 235) solid;
  border-radius: 1vmin;
  font-size: 1.1rem;
  outline-color: lightblue;
`;
const Input = styled.input`
  box-sizing: border-box;
  width: 100%;
  padding: 0.9rem 0.5rem;
  background-color: #f4f5f7;
  border: 1px rgb(229, 231, 235) solid;
  border-radius: 1vmin;
  font-size: 1.1rem;
  outline: none;

  :focus {
    background-color: white;
  }
`;

const TagSection = styled.div`
  display: flex;
  background-color: #f4f5f7;
  flex-wrap: wrap;

  > input {
    border-radius: 0;
    border: none;
  }
`;

const Tag = styled.div`
  margin: 0.8rem 0.4rem;
  background-color: #e1e1e1;
  background-color: ${(p) => (p.color ? p.color : "#E1E1E1")};

  display: flex;
  width: max-content;

  > :first-child {
    color: ${(p) => (p.color ? "white" : "black")};
    margin: auto;
    padding: 0 0.5rem;
    font-size: 0.8rem;
  }

  > :last-child {
    font-size: 0.8rem;
    background-color: #d4d4d4;
  }
`;

function Addserviceorder({ isOpen, setIsOpen, EditProductInfo, title, desc }) {
  const DefaultValues = {
    title: "",
    productno: "",
    size: [],
    color: [],
    desc: "",
    categories: [],
    quantity: "",
    price: "",
    img: null,
  };

  const dispatch = useDispatch();
  const [Product, setProduct] = useState(DefaultValues);

  useEffect(() => {
    if (!EditProductInfo) return;
    setProduct({ ...EditProductInfo });
  }, [EditProductInfo]);

  const handleChange = (e, type) => {
    const { name, value } = e.target;
    const property = type || name;
    console.log("input changed on " + title);

    if (name === "img") {
      const file = e.target.files[0];
      const filereader = new FileReader();
      filereader.readAsDataURL(file);
      filereader.onload = () => {
        console.log("image changed on " + title);
        setProduct((p) => ({ ...p, img: filereader.result }));
      };
      return;
    }

    const prev = Product[property]; //we ddnt used Product.property bcz iw will find a field where the key is property but in this cal it will find the value of property
    if (Array.isArray(prev)) {
      const exist = prev.filter((i) => i === value.toUpperCase());
      if (exist?.length) return; //TODO: add Errors
    }
    setProduct((p) => ({
      ...p,
      [property]: Array.isArray(prev) ? [...prev, value.toUpperCase()] : value,
    })); //if it's array then append or setValue

    e.target.value = "";
  };

  const handleSubmit = async () => {
    if (!EditProductInfo) {
      console.log("editProduct");
      addProductapi(dispatch, Product, setIsOpen);
    } else {
      console.log("no editProduct");
      editProductapi(dispatch, Product, setIsOpen);
    }
  };

  const handleDelete = (property, value) => {
    setProduct((p) => ({
      ...p,
      [property]: p[property].filter((i) => i !== value),
    }));
  };

  useEffect(() => {
    console.log(Product);
  }, [Product]);

  const ifForImf = Math.random() * 1000; //this is used Because Img id was chasing bcz this component was rendered 2 time 1 for add and another for update
  return (
    <EditModal
      isOpen={isOpen}
      setIsOpen={setIsOpen}
      action={handleSubmit}
      title={title}
      desc={desc}
    >
      <Container>
        
        <Section>
          <Left>
            <label>Service Name</label>
          </Left>
          <Right>
            <Input
              name="title"
              value={Product.title}
              onChange={(e) => handleChange(e)}
            />
          </Right>
        </Section>

        <Section>
          <Left>
            <label>Product Number</label>
          </Left>
          <Right>
            <Input
              name="productno"
              value={Product.productno}
              onChange={(e) => handleChange(e)}
            />
          </Right>
        </Section>

        <Section>
          <Left>
            <label>Product Size</label>
          </Left>
          <Right>
            <TagSection>
              {Product?.size?.map((s) => {
                return (
                  <Tag key={s}>
                    <span>{s}</span>
                    <div onClick={() => handleDelete("size", s)}>
                      <CloseOutlinedIcon />
                    </div>
                  </Tag>
                );
              })}
              <Input
                placeholder="Sizes (Write and press Enter)"
                name="size"
                onKeyDown={(e) => {
                  if (e.key === "Enter") handleChange(e, "size");
                }}
              />
            </TagSection>
          </Right>
        </Section>

        <Section>
          <Left>
            <label>Product Color</label>
          </Left>
          <Right>
            <TagSection>
              {Product?.color?.map((s) => {
                return (
                  <Tag key={s} color={s}>
                    <span>{s}</span>
                    <div onClick={() => handleDelete("color", s)}>
                      <CloseOutlinedIcon />
                    </div>
                  </Tag>
                );
              })}
              <Input
                style={{ height: "80px" }}
                type="color"
                placeholder="Sizes (Write and press Enter)"
                name="color"
                onKeyDown={(e) => {
                  if (e.key === "Enter") handleChange(e, "color");
                }}
              />
            </TagSection>
          </Right>
        </Section>

        <Section>
          <Left>
            <label>Product Description</label>
          </Left>
          <Right>
            <Textarea
              name="desc"
              value={Product.desc || ""}
              onChange={(e) => handleChange(e)}
            />
          </Right>
        </Section>

        <Section>
          <Left>
            <label>Product Category</label>
          </Left>
          <Right>
            <TagSection>
              {Product?.categories?.map((s) => {
                return (
                  <Tag key={s}>
                    <span>{s}</span>
                    <div onClick={() => handleDelete("categories", s)}>
                      <CloseOutlinedIcon />
                    </div>
                  </Tag>
                );
              })}
              <Input
                placeholder="Categories (Write and press Enter)"
                name="categories"
                onKeyDown={(e) => {
                  if (e.key === "Enter") handleChange(e, "categories");
                }}
              />
            </TagSection>
          </Right>
        </Section>

        <Section>
          <Left>
            <label>Product Quantity</label>
          </Left>
          <Right>
            <Input
              type="number"
              name="quantity"
              value={Product.quantity}
              onChange={(e) => handleChange(e)}
            />
          </Right>
        </Section>

        <Section>
          <Left>
            <label>Product Price</label>
          </Left>
          <Right>
            <Input
              type="number"
              name="price"
              value={Product.price}
              onChange={(e) => handleChange(e)}
            />
          </Right>
        </Section>
      </Container>
    </EditModal>
  );
}

export default Addserviceorder;
