import React, {useEffect, useState} from 'react'
import styled from 'styled-components'
import { mobile } from '../Responsive'
import { Link } from 'react-router-dom'
import { login } from '../redux/apiCalls/auth.js'
import { useDispatch, useSelector } from 'react-redux'

//import { resetError } from '../redux/userRedux'

const Container = styled.div`
    position: relative;
    width: 100vw;
    height: ${props => props.is ? "calc(100vh - 60px)" : "100vh"}; //60px of navbar
    display: flex;
    justify-content: center;
    align-items: center;
    background-image: linear-gradient(
      rgba(255,255,255, 0.5),
      rgba(255,255,255, 0.5)
      ),
      url("https://images.pexels.com/photos/131634/pexels-photo-131634.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1");
    
    
`
const Wrapper = styled.div`
    width: min(400px, 80%);
    padding: 40px 30px;
    background-color: white;
    display: flex;
    flex-direction: column;
    border-radius: 1vmax;
    box-shadow: 20px 20px 50px grey;
    ${mobile({
      padding: "20px 15px"
    })}
    
    
`
const Title = styled.h1`
  font-size: 24px;
  font-weight: 300;
  margin-bottom: 10px;
`
const Form = styled.form`
 // width: 100%;
  display: flex;
  flex-wrap: wrap;
`


const Input = styled.input`
  min-width: 80%;
  padding: 10px;
  margin: 15px 0;
  flex: 1;
  display: flex;
  min-height: 40%;
  border-radius: 0.5vmax;
  ${mobile({
    margin: "15px 0px",
  })}
`


const Button = styled.button`
  margin: 10px 0px;
  width: 40%;
  border: none;
  background-color: teal;;
  padding: 15px 20px;
  background-color: teal;
  color: white;
  border-radius: 1vmin;
  margin-right: 60% ;
  &:disabled{
    color: green;
    background-color: #e1e6ed;
    cursor: not-allowed;
  }
`
const HelpLink = styled.a`
    margin: 5px 0px; 
`
const Error = styled.span`
    color: red;
`


function Login(props) {

  //to change title as soon as component mounts
  useEffect(() => {
    document.title = `Name - ${props.title}`
  }, []) // eslint-disable-line react-hooks/exhaustive-deps

  const user = useSelector(state => state.user.currentUser) //is logedin user?
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { isFetching } = useSelector(state => state.user)

  const dispatch = useDispatch()

  

  const submit = async (e) => { 
    e.preventDefault();
    console.table(email, password)
    
    login( dispatch ,{ email , password })

  }

  return (
    <>
    <Container is={user ? true : false}>
        <Wrapper>
            <Title>Login</Title>
            <Form autoComplete='on' onSubmit={submit}>
                <Input type="email" placeholder='Email' onChange={(e)=> setEmail(e.target.value)}></Input>
                <Input type="password" placeholder='Password' onChange={(e)=> setPassword(e.target.value)} autoComplete="off"></Input>
                <HelpLink target="_blank" style={{marginLeft:"auto", marginRight:"0" }}><a href={`${process.env.REACT_APP_MAIN_SITE_URL}/forgotpassword`}>Forgot your password?</a></HelpLink>
                <Button  >Login</Button>
            </Form>
            
            {/* {console.log(error.error)} */}
            
            {/* <HelpLink><Link to="/forgotpassword">Do note remember your Password?</Link></HelpLink> */}
            <HelpLink><Link to="/signup">Create New Account</Link></HelpLink>
        </Wrapper>
        
    </Container>
    </>
  )
}

export default Login
