import { useState, useEffect } from "react";
import AddIcon from "@mui/icons-material/Add";
import EditModal from "./EditModal";
import styled from "styled-components";
import CloudUploadOutlinedIcon from "@mui/icons-material/CloudUploadOutlined";
import CloseOutlinedIcon from "@mui/icons-material/CloseOutlined";
import { useDispatch } from "react-redux";
import { addProductapi, editProductapi } from "../redux/apiCalls/productsApis";



function Addserviceorder({ isOpen, setIsOpen, EditProductInfo, title, desc }) {

  const [data, setdata] = useState({
    lname: "",
    service: "",
    startdate: "",
    enddate: "",
    amount: "",
  });

  const DefaultValues = {
    title: "",
    productno: "",
    size: [],
    color: [],
    desc: "",
    categories: [],
    quantity: "",
    price: "",
    img: null,
  };

  const dispatch = useDispatch();
  const [Product, setProduct] = useState(DefaultValues);

  
  

  const handleDelete = (property, value) => {
    setProduct((p) => ({
      ...p,
      [property]: p[property].filter((i) => i !== value),
    }));
  };

  useEffect(() => {
    console.log(Product);
  }, [Product]);

  const ifForImf = Math.random() * 1000; //this is used Because Img id was chasing bcz this component was rendered 2 time 1 for add and another for update
  return (
    <div className="maincontainer">
      <h1>Add Ordder</h1>
      <div className="inputfield">
        <label>Enter Library Name </label>
        <input required type="text" />
      </div>
      <div className="inputfield">
        <label>Enter Service name </label>
        <input required type="text" />
      </div>
      <div className="inputfield">
        <label>Start Date of service </label>
        <input required type="date" />
      </div>
      <div className="inputfield">
        <label>End Date of service </label>
        <input required type="date" />
      </div>
      <div className="inputfield">
        <label>Amount </label>
        <input required type="number" />
      </div>
      <div className="inputfield">
        <button
          type="button"
          onClick={handleSubmit}
          className="btn "
        >
          Add <AddIcon />
        </button>
      </div>
    </div>
  );
}

export default Addserviceorder;
